@use '../utils';

.header {
    background-image: url("../images/bg-hero-mobile.svg");
    background-repeat: no-repeat;
    background-size: cover;
    background-color: utils.$bg-lime;
}

.logo__link {
    &:focus,
    &:hover {
        outline: 2px dotted utils.$bg-btn-pink;
    }
}

.logoSection {
    padding: 1rem 1.5rem;
    display: flex;
    justify-content: space-between;
    align-items: center;
    max-width: 1280px;
    margin: auto;
}

.logo {
    width: 65%;
    max-width: 200px;
}

.tryIt,
.logo__link {
    flex: 1 1 50%;
}

.tryIt {
    display: flex;
    justify-content: flex-end;
    align-items: center;
}

// hero section
.hero {
    padding: 3.5rem 2.25rem 2.5rem;
    margin: auto;
    text-align: center;
    max-width: 1270px;
}

.hero__summary {
    margin: 2rem auto;
}

.build {
    max-width: 460px;
    margin: auto;
}

.img__container {
    max-width: 460px;
    margin: 3rem auto;
}


@include utils.mQ(1080px) {
    .header {
        background-image: url("../images/bg-hero-desktop.svg");
        padding: 3rem 5rem 1rem;
        text-align: left;
    }

    .logoSection {
        padding: 1rem 1.5rem 0;
    }

    .logo__link {
        &:focus,
        &:hover {
            outline: 4px dotted utils.$bg-btn-pink;
        }
    }

    .hero {
        display: flex;
        justify-content: space-between;
        align-items: center;
        text-align: left;
        padding: 0 0 2.5rem;
    }

    .build {
        margin: 0;
        max-width: 536px;
        flex: 1 1 40%;
    }

    .img__container {
        margin: 3rem 0;
        max-width: 680px;
        flex: 1 1 60%;
    }

}

