@use '../utils';

.footer {
    background-color: #00252e;
    color: utils.$tx-white;
    padding: 8rem 1rem 6rem;
}

.footer__wrapper {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    align-items: baseline;
    gap: 25px;
}

.footer__logo {
    margin-bottom: 2rem;
}

.logoFooter {
    transform: scale(.6);
    margin-left: -48px;
    padding-left: 0;
    display: block;
}

.logoFooter path {
    fill: utils.$tx-white;
}

.contact {
    display: flex;
    justify-content: flex-start;
    align-items: baseline;
    margin-top: 1rem;
}

.contact__address {
    font-size: 0.875rem;
    margin-left: 2rem;
}

.about__us {
    display: flex;
    flex-direction: column;
    margin-top: 1rem;

    a {
        text-decoration: none;
        color: white;

        &:hover,
        &:focus {
            border-bottom: 2px solid white;
        }
    }
}

.fa {
    &:hover, &:focus {
        filter: invert(42%) sepia(93%) saturate(375%) hue-rotate(300deg);
        text-decoration: none;
    }
}

.social__icons {
    width: 32px;
    height: 32px;
    display: flex;
    border-radius: 50%;
    border: 1px solid white;
    justify-content: center;
    align-items: center;

    /*
    &:hover {
        border: 1px solid utils.$bg-btn-pink;
    }
    */  



}

.social {
    width: 120px;
    display: flex;
    margin: 3rem auto;
    justify-content: space-between;
}

.fa {
    filter: invert(100%) sepia(100%) saturate(0%);
    // margin-right: 20px;
    text-decoration: none;
}

li:not(:last-child) {
    margin-bottom: 15px;
}

.copy__right {
    text-align: center;
}

@include utils.mQ(1080px) {
    .footer__wrapper {
        max-width: 1280px;
        padding: 1rem 5rem;
        margin: auto;
    }

    .social {
        margin: 0 auto;
    }

    .about__us {
        align-items: center;
    }
}

