@use 'sass:color';
@use '../utils';
.btn__try {
    background-color: utils.$bg-white;
    font-size: 0.625rem;
    padding: .5rem 1.5rem;
    border-radius: 24px;
    font-weight: 700;
    color: utils.$tx-green-black;
    text-decoration: none;
    text-align: center;
    border: 2px solid transparent;

    &:hover, &:focus {
        outline: 2px solid utils.$tx-mid-grey;        
    };
}

.get__started {
    background-color: utils.$bg-btn-pink;
	padding: .8rem 2rem;
	width: 80%;
	display: block;
	margin: auto;
	max-width: 240px;
    font-size: 0.75rem;
    color: utils.$tx-white;
    &:hover, &:focus {
        background-color: color.scale(utils.$bg-btn-pink, $lightness: 30%);        
    };
  
}

@include utils.mQ(1080px) {
    .btn__try {           
        padding: 0.4rem 3rem;    
        font-size: 1.25rem;   
        border-radius: 40px;            
        border: 4px solid transparent;
        &:hover, &:focus {
            outline: 4px solid utils.$tx-mid-grey;
        };
    }
    .get__started {
        max-width: 400px;
    }
    .hero__btn {
        margin-left: 0;
    }
}
